<?php

//  ------------------------------------------------------------------
//  DIRECTORYLIST function
//	Lists all files from a directory. Set recurse=true for subdirs included.

function directorylist($dir, $recurse=false)
  {
  
  if (!file_exists($dir)) { return false; }

// array to hold return value

    $retval = array();

// add trailing slash if missing

    if(substr($dir, -1) != "/") $dir .= "/";

// open pointer to directory and read list of files

    $d = @dir($dir) or die();
    while(false !== ($entry = $d->read())) {

// skip hidden files

      if($entry[0] == '.') continue;
      if(is_dir("$dir$entry")) {
        $retval[] = array(
          "name" => "$dir$entry/",
          "type" => filetype("$dir$entry"),
          "size" => 0,
          "lastmod" => filemtime("$dir$entry")
        );

       if($recurse && is_readable("$dir$entry/")) {
          $retval = array_merge($retval, directorylist("$dir$entry/", true));
        }
      } elseif(is_readable("$dir$entry")) {
        $retval[] = array(
          "name" => "$dir$entry",
          "filename" => "$entry",
          "size" => filesize("$dir$entry"),
          "lastmod" => filemtime("$dir$entry")
        );
      }
    }
    $d -> close();

    return $retval;
  }

//  -------------------------------------------------------------------------------------------------------------------------------------------------------

  if (isset($_POST['ajax'])) {

//  unzip pgCMS

    if ($_POST['ajax'] == 'unzip') {
    
        if (!file_exists('pgcms.zip')) { die('<span style="color: #FF0000;">ERROR:</span> pgcms.zip not found or not readable!'); }
        $zip = zip_open('pgcms.zip');
        if ($zip) {
          while ($zip_entry = zip_read($zip)) {
            
            $filename = basename(zip_entry_name($zip_entry));
            $directory = substr(zip_entry_name($zip_entry), 0, (strlen(zip_entry_name($zip_entry)) - strlen($filename)));
            $currentdir = getcwd();

            $dirlist = explode('/', $directory);
            foreach($dirlist as $dir) {
              if (!chdir($dir) && strlen($dir) != 1) {
                mkdir($dir);
                chdir($dir);
              }
            }
            
            while(getcwd() != $currentdir) { chdir('..'); }
            
            $fp = fopen(zip_entry_name($zip_entry), "w");
            
            if (zip_entry_open($zip, $zip_entry, "r")) {
              $buf = zip_entry_read($zip_entry, zip_entry_filesize($zip_entry));
              if (zip_entry_filesize($zip_entry) != 0) { fwrite($fp, $buf); }
              zip_entry_close($zip_entry);
            }
            fclose($fp);
          }
          zip_close($zip);
          die('DONE');
        } else {
          die('<span style="color: #FF0000;">ERROR:</span> unable to unpack pgcms.zip!');
        }

    }

//  set file and directory attributes

    if ($_POST['ajax'] == 'setattributes') {

      $dirlist = directorylist('.');
      foreach($dirlist as $file) {
        if ($file->size == 0) chmod($file->filename, octdec(751));
      }

      $dirlist = directorylist('userdata');
      foreach($dirlist as $file) {
        if ($file->size == 0) chmod($file->filename, octdec(751));
      }
    
      $dirlist = directorylist('xmldata');
      foreach($dirlist as $file) {
        chmod('xmldata/'.$file->filename, octdec(755));
        if ($file->filename == 'settings.xml') chmod('xmldata/'.$file->filename, octdec(664));    
        if ($file->filename == 'languages.xml') chmod('xmldata/'.$file->filename, octdec(664));    
        if ($file->filename == 'countries.xml') chmod('xmldata/'.$file->filename, octdec(664));    
        if (substr($file->filename, 0, 6) == 'emails') chmod('xmldata/'.$file->filename, octdec(664));    
      }

      die('DONE');
    }

//  creating database and dbsettings.php file

    if ($_POST['ajax'] == 'createdatabase') {

       $dbsettings = "<?php\n".
                     "    \$GLOBALS['database_server'] = '".$_POST['dbserver']."';\n".
                     "    \$GLOBALS['database_port'] = '".$_POST['dbport']."';\n".
                     "    \$GLOBALS['database_user'] = '".$_POST['dbuser']."';\n".
                     "    \$GLOBALS['database_password'] = '".$_POST['dbpass']."';\n".
                     "    \$GLOBALS['database_name'] = '".$_POST['dbname']."';\n".
                     "?>";
       chmod('api', octdec(776));
       if (!file_put_contents('api/dbsettings.php', $dbsettings)) { die('<span style="color: #FF0000;">Could not create api/dbsettings.php!</span>'); }
       chmod('api', octdec(755));

       $dbconn = pg_connect('host='.$_POST['dbserver'].'
                             port='.$_POST['dbport'].' 
                             user='.$_POST['dbuser'].' 
                             password='.$_POST['dbpass'])
                             or die('<span style="color: #FF0000;">Unable to connect PostgreSQL.</span> '.pg_last_error());
                             
       $data = pg_query($dbconn, 'CREATE DATABASE '.$_POST['dbname'].' ENCODING \'UTF8\'');
       if (!$data) { die('<span style="color: #FF0000;">PostgreSQL error:</span> '.pg_last_error()); }
       die('DONE');
    }
    
    die('WTF');

  }

?>

<!DOCTYPE html>
<html lang="en">
  <head>
    <meta charset="utf-8">
