    <title>pgCMS installer</title>

<script>

function unzip() {

   $('#log').prepend('Unzipping pgcms.zip, please wait...<br />');

   $.ajax({
      type: 'POST',
      cache: 'false',
      url: 'install.php',
      data: 'ajax=unzip',
      dataType: 'text',

      complete: function(xhr) {
      
        if (xhr.responseText == 'DONE') {
          $('#log').prepend('Unzip successful.<br />');
          setattributes();
        } else {
          $('#log').prepend(xhr.responseText+'<br />');
        }
      }
  });
}

function setattributes() {
   $('#log').prepend('Setting file attributes...<br />');
   $.ajax({
      type: 'POST',
      cache: 'false',
      url: 'install.php',
      data: 'ajax=setattributes',
      dataType: 'text',

      complete: function(xhr) {
      
        if (xhr.responseText == 'DONE') {
          $('#log').prepend('Attributes successfully set.<br />');
          $('#dbsettings').css('top', String(($(window).height() / 2) - ($('#dbsettings').outerHeight() / 2))+'px');
          $('#dbsettings').css('left', String(($(window).width() / 2) - ($('#dbsettings').outerWidth() / 2))+'px');
          $('#dbsettings').css('visibility', 'visible');
        } else {
          $('#log').prepend(xhr.responseText+'<br />');
        }
      }
  });
}

function createdatabase() {
   $('#dbsettings').css('visibility', 'hidden');
   $('#log').prepend('Creating database...<br />');
   $.ajax({
      type: 'POST',
      cache: 'false',
      url: 'install.php',
      data: 'ajax=createdatabase&'+
            'dbname='+encodeURIComponent($('#dbname').val())+'&'+
            'dbuser='+encodeURIComponent($('#dbuser').val())+'&'+
            'dbpass='+encodeURIComponent($('#dbpass').val())+'&'+
            'dbserver='+encodeURIComponent($('#dbserver').val())+'&'+
            'dbport='+encodeURIComponent($('#dbport').val()),
      dataType: 'text',

      complete: function(xhr) {
      
        if (xhr.responseText == 'DONE') {
          $('#log').prepend('Database successfully created.<br />');
        } else {
          $('#log').prepend(xhr.responseText+'<br />');
        }
        
        $('#log').prepend('Install complete - now please import SQL dump manually.<br />');
      }
  });
}

</script>
    
